import {Types} from "./Redux.actions";
const INITIAL_STATE = {
    men: [{
        name: 'Royale',
        imgUrl: 'Mens-193-Royale-Chocolate.jpg',
        color:  'Navy',
        price: '140$',
    } , {
        name: 'Royale',
