        color:  'Bianco',
        price: '210$',
    } , {
        name: 'Queen',
        imgUrl: 'Womens-193-Royale-Perforated-Blush.jpg',
        color:  'Nero',
        price: '550$',
    } , {
        name: 'Court',
        imgUrl: 'Womens-194-RoyaleKnit-Grey.jpg',
        color:  'Red',
        price: '320$',
    }],
    active : [{
        name: 'Royale',
        imgUrl: 'Mens-193-Royale-Chocolate.jpg',
        color:  'Navy',
        price: '140$',
    } , {
        name: 'Royale',
        imgUrl: 'Mens-193-Royale-Blanco.jpg',
        color:  'Bianco',
        price: '120$',
    } , {
        name: 'Royale',
        imgUrl: 'Mens-193-Royale-Nero.jpg',
        color:  'Nero',
        price: '110$',
    } , {
        name: 'Court',
        imgUrl: 'Mens-193-Royale-Cuoio.jpg',
        color:  'Gray',
        price: '220$',
    },],
    activated : "men",
    cart: {},
    showSidebar : false
};

export default function (state = INITIAL_STATE, action) {
    switch (action.type) {
        case Types.setActive:
            return {
                ...state,
                active : action.active,
                activated : action.activated
            };
        case Types.addToCart:
            return {
                ...state,
                cart: {...state.cart, ...action.cart},
            };
        case Types.showSidebar:
            console.log(state);
            return {
                ...state,
                showSidebar : action.showSidebar,
            };
        default: {
            return {
                ...state
            }
        }
    }
}
