import React from 'react'

export default class GalleryItem extends React.Component {
    constructor(props) {
        super ( props );
        this.state = {
            expand : false,
            isExpanded:false
        };
        this.myHandler = this.myHandler.bind ( this );

    }

    myHandler() {
        console.log("clicked!");
        this.setState({expand : false})
    }
    componentDidMount(){
        document.body.addEventListener('click', this.myHandler);
    }
    render() {

        return (<div
            className={"gallery-item margin-10 gallery-item " + (!!this.state.expand ? "gallery-item-active" : "")}
            style={{
                backgroundImage : 'url(/assets/' + this.props.url + ')'
            }}

            onClick={() => {
                !!this.state.isExpanded ? this.setState ( { isExpanded : false } ) : this.setState ( { isExpanded : true } );
                !!this.state.expand&&!!this.state.isExpanded ? this.setState ( { expand : false } ) : this.setState ( { expand : true } )
            }}
            onBlur={() => {
                console.log ( "blur" );
                this.setState ( { expand : false } )
            }}

        />)


    }
}
