import React from "react";
import './App.scss';
import {addToCart} from "./services/Redux.actions";
import {connect} from "react-redux";
import {CartItem} from "./cart-item";

class SidebarComponent extends React.Component {
    constructor(props){
        super(props)
    }
    render() {
        var items = [];
        function renderItems(){
            for (let item in this.props.cart) {
                if (!!this.props.cart && this.props.cart.hasOwnProperty ( item )) {
                    items.push (
                        <CartItem
                            key={item.name + item.color}
                            name={item.name}
                            color={item.color}
                            price={item.price}
                            image={item.imgUrl}
                        />

                    )
                }
            }
            return items
        }
        return (
            <div className={"sidebar"}>
                {renderItems()}
            </div>)
    }
}

const mapStateToProps = (state) => {
    return ({
        men : state ? state.men : '' ,
        women : state ? state.women : '' ,
        active : state ? state.active : '' ,
        activated : state ? state.activated : '' ,
        cart : state ? state.cart : '[]'
    });
};
const mapDispatchToProps = (dispatch) => {
    return ({
        addToCart : (params) => {
            dispatch ( addToCart ( params ) );
        }
    })
};
export const Sidebar = connect ( mapStateToProps , mapDispatchToProps ) ( SidebarComponent );
