import React from 'react'
import './App.scss';
import {connect} from "react-redux";

class GalleryComponent extends React.Component {
    constructor(props) {
        super ( props )
    }

    render() {
        return (
            <div className={"no-overflow flex-center vertical-margin-50"}>
                <div className={"flex no-wrap article-list-inner article-container"}>
                    {
                        // eslint-disable-next-line array-callback-return
                        this.props.gallery.map ( (item) => {
                            return (
                                <div
                                    style={{
                                    backgroundImage : 'url(/assets/' + item.url + ')' ,
                                    paddingTop : "20%",
                                        minWidth:"200px",
                                        height:"0"
                                }}
                                     key={item.imgUrl}
                                />)

                        } )
                    }
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return ({
        gallery : state ? state.gallery : '[]'
    });
};


export const Gallery = connect ( mapStateToProps ) ( GalleryComponent );
