import React from "react";
import './App.scss';
import {connect} from "react-redux";
import Article from "./article-component";
import {setActive} from "./services/Redux.actions";

class ArticleListComponent extends React.Component {
    constructor(props) {
        super ( props );
        this.state = {}
    }
componentDidMount() {
        if(this.props.active){
        this.setState(
            {active : this.props.active}
        )} else {
            this.setState(
                {active : this.props.men}
            )
        }
}

    render() {
        console.log ( this.props );
        return (
            <div>
                <div className={"flex-center"}>
                    <div className={"horizontal-margin-10"}
                         onClick={() => {
                             this.props.setActive ( {
                                 active : this.props.men ,
                                 activated : "men"
                             } );
                             console.log ( this.props.activated )
                         }}
                    ><h2>Men</h2>
                    </div>
                    <div className={"horizontal-margin-10"}
                         onClick={() => {
                             this.props.setActive ( {
                                 active : this.props.women ,
                                 activated : "women"
                             } );
                             console.log ( this.props.activated )
                         }}
                    ><h2>Women</h2>
                    </div>
                </div>
                <div className={"article-list"}>
                    {
                        this.state.active.map ( (item) => {
                            if (item.name !== 'Court') {
                                return (
                                    <Article
                                        key={item.name + item.color}
                                        name={item.name}
                                        color={item.color}
                                        price={item.price}
                                        image={item.imgUrl}
                                    />

                                )
                            }
                        } )
                    }
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return ({
        men : state ? state.men : '' ,
        women : state ? state.women : '' ,
        active : state ? state.active : '' ,
        activated : state ? state.activated : '' ,
    });
};
const mapDispatchToProps = (dispatch) => {
    return ({
        setActive : (params) => {
            dispatch ( setActive ( params ) );
        }
    })
}
export const ArticleList = connect ( mapStateToProps , mapDispatchToProps ) ( ArticleListComponent );
