import React from "react";
import './App.scss';
import {connect} from "react-redux";
import Article from "./article-component";

class ArticleListComponent extends React.Component {
    constructor(props) {
        super ( props );
    }

    render() {
        console.log(this.props);
        function returnArticle(item){
            return(
                <Article
                    name = {item.name}
                    color = {item.color}
                    price = {item.price}/>
            )
        }
        var allArticles =  this.props.men.map(returnArticle);
        console.log(allArticles);
        return (
            {allArticles}
            )
    }
}

const mapStateToProps = (state) => {
    return ({
        men : state ? state.men : '' ,
        women : state ? state.women : '' ,
    });
};
export const ArticleList = connect ( mapStateToProps) ( ArticleListComponent );
