import React from 'react'


export default class Badge extends React.Component{
    constructor(props){
        super(props)
    }
    render(){
        return (
            <div className={"flex  flex-column"}>
                <h3>{this.props.title}</h3>
                <p>{this.props.text}</p>
            </div>
        )
    }
}
