import React from 'react'
import ReactDOM from "react-dom";
import './App.scss';
import {connect} from "react-redux";
import GalleryItem from "./gallery-item";

class GalleryComponent extends React.Component {
    constructor(props) {
        super(props);
        this.ref = React.createRef();
        this.state = {
            isScrolling: false,
            clientX: 0,
            scrollX: 0,
        };
    }

    onMouseDown = e => {
        this.setState({ ...this.state, isScrolling: true,
            clientX: e.clientX });
    };

    onMouseUp = () => {
        this.setState({ ...this.state, isScrolling: false });
    };

    onMouseMove = e => {
        const { clientX, scrollX } = this.state;
        if (this.state.isScrolling) {
            this.ref.current.scrollLeft = scrollX + e.clientX - clientX;
            this.state.scrollX = scrollX + e.clientX - clientX;
            this.state.clientX = e.clientX;
        }
    };

    render() {
        return (
            <div className={"no-overflow flex-center vertical-margin-50"}>
                <div className={"flex no-wrap article-list-inner inner-gallery"}
                     ref={ref}
                     onMouseDown={this.onMouseDown}
                     onMouseUp={this.onMouseUp}
                     onMouseMove={this.onMouseMove}
                >
                    {
                        this.props.gallery.map ( (item) => {
                            return (
                                <GalleryItem
                                    url={item.url}/>)
                        } )

                    }

                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return ({
        gallery : state ? state.gallery : '[]'
    });
};
GalleryComponent.defaultProps = {
    ref: { current: {} },
    rootClass: '',
};

export const Gallery = connect ( mapStateToProps ) ( GalleryComponent );
