import React from "react";
import './App.scss';
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {connect} from "react-redux";
import {setActive} from "./services/Redux.actions";
class HeaderComponent extends React.Component {
    constructor(props) {
        super ( props );
    }

    render() {
        return (<div
            className={"flex space-between header"}>
            {/*left tabs*/}
            <div className={"flex-center left"}>
                <div className={"horizontal-margin-10 bold-white grow-1"}><h2>GREATS</h2></div>
                <div className={"horizontal-margin-10 small-white grow-1"}
                     onClick={() => {
                         this.props.setActive ( { active : this.props.men } );
                        console.log(this.props.active)
                     }}
                ><h2>Men</h2>
                </div>
                <div className={"horizontal-margin-10 small-white grow-1"}
                     onClick={() => {
                         this.props.setActive ( { active : this.props.women } );
                         console.log(this.props.active)
                     }}
                ><h2>Women</h2>
                </div>
            </div>
            {/*right tabs*/}
            <div className={"flex-center right"}>
                <div className={"horizontal-margin-10 grow-1"}><a className={"small-white"} href={'https://oykos.me/hub'} target="_blank" rel="noopener noreferrer"><h2>Our Story</h2></a></div>
                <div className={"horizontal-margin-10 grow-1"}><a className={"small-white"} href={'https://oykos.me/hub'} target="_blank" rel="noopener noreferrer"><h2>Visit Us</h2></a></div>
                <div className={"horizontal-margin-10 grow-1"}><a className={"small-white"} href={'https://oykos.me/hub'} target="_blank" rel="noopener noreferrer"><h2>My Account</h2></a></div>
                <div className={"horizontal-margin-10 grow-1"}><a className={"small-white"} href={'https://oykos.me/hub'} target="_blank" rel="noopener noreferrer"><FontAwesomeIcon style={{fontSize:"14px"}} icon={faSearch} /></a></div>
                <div className={"horizontal-margin-10 grow-1"}><a className={"small-white"} href={'https://oykos.me/hub'} target="_blank" rel="noopener noreferrer"><h2>Shopping Cart</h2></a></div>
            </div>
        </div>)
    }
}

const mapStateToProps = (state) => {
    return ({
        men : state ? state.men : '' ,
        women : state ? state.women : '' ,
        active : state ? state.active : ''
    });
};
const mapDispatchToProps = (dispatch) => {
    return ({
        setActive : (params) => {
            dispatch ( setActive ( params ) );
        }})
}
export const Header = connect ( mapStateToProps, mapDispatchToProps) ( HeaderComponent );
