import React from "react";
import './App.scss';
import {connect} from "react-redux";
import Article from "./article-component";

class ArticleListComponent extends React.Component {
    constructor(props) {
        super ( props );
        this.state = {active : []}
    }
    componentDidMount() {
        if(!this.props.active){
        this.setState({ active: this.props.men })
        } else {
            this.setState({ active: this.props.active })
        };
    }
    render() {
        console.log('this.state.active');
        console.log(this.state.active);
        return (
            <div className={"article-list"}>
            {
                this.state.active.map ( (item) => {
                    if(item.name!=='Court'){
                    return (
                        <Article
                            key = {item.name+item.color}
                            name={item.name}
                            color = {item.color}
                            price = {item.price}
                            image = {item.imgUrl}
                        />

                    )
                    }
                } )
            }
            </div>
            )
    }
}

const mapStateToProps = (state) => {
    return ({
        men : state ? state.men : '' ,
        women : state ? state.women : '' ,
    });
};
export const ArticleList = connect ( mapStateToProps) ( ArticleListComponent );
