import React from "react";
import './App.scss';
import Button from './Button'
export default class Hero extends React.Component {
    constructor(props) {
        super ( props );
    }

    render() {
        console.log(this.props);
        return (<div className={"hero"}>
            <div className={"margin-20-10prc"}>
            <h1 className={"bold-white xxl-font"}>The Royale Navy</h1>
            <p className={"regular-white bold-white"}>Our best selling Navy blue color way is back in limited quanities. Preorder now available</p>
                <Button
                text={"Shop Now"}
                />
            </div>

        </div>)
    }
}


const mapStateToProps = (state) => {
    return ({
        template : state ? state.email : '' ,
        request : state ? state.request : '' ,
    });
};
