import React from 'react'

export default class GalleryItem extends React.Component {
    constructor(props) {
        super ( props );
        this.state = {
            expand : this.props.state
        }
    }



render ()
{
    const handleBlur = function () {
        this.setState ( { expand : false } );
    };
    return (
        <div className={"gallery-item margin-10 gallery-item " + (!!this.state.expand ? "gallery-item-active" : "")}
             style={{
                 backgroundImage : 'url(/assets/' + this.props.url + ')'
             }}
             onClick={() => {
                 !!this.state.expand ? this.setState ( { expand : false } ) : this.setState ( { expand : true } )
             }}
             onBlur={ () => {console.log('input', 'blur');} }

        />
    )


}
}
