import React from 'react'
import Button from "./Button";

export default class Footer extends React.Component {
    constructor(props) {
        super ( props )
    }

    render() {
        return (
            <div className={"flex-center space-around bgd-black vertical-padding-50"}
                 style={{ alignItems : "baseline" }}>
                <div className={"flex-column"}>
                    <h2 className={""}>Get Updates</h2>
                    <p>Sign up for Greats news and receive 25% off your first purchase.</p>
                    <div className={"flex-center"}>
                        <input
                            type={"email"}
                            placeholder={"Email Address"}
                            className={"white-border2 button bgd-black"}/>
                        <Button text={"Sign Up"}
                                class={"bgd-white white-border2"}/>
                    </div>
                </div>
            </div>
        )
    }
}
