import React from "react";
import './App.scss';
export default class Article extends React.Component {
    constructor(props) {
        super ( props );
    }

    render() {
        let imgUrl = this.props.image;
        let imageName = require(imgUrl);
        return (
            <div className={"article"}>
                <img src={imageName} alt={this.props.name +" " + this.props.color}/>
                {this.props.name}
                {this.props.color}
                {this.props.price}
            </div>)
    }
}
