import React from "react";
import './App.scss';
import {connect} from "react-redux";
import {addToCartAction} from "./services/Redux.actions";

class ArticleComponent extends React.Component {
    render() {
        console.log(this.props);
        return (
            <div className={"article"}
                 onClick={() => {
                     this.props.addToCartAction ( {
                         cart : [
                             ...this.props.cart ,
                             {
                                 name : this.props.name ,
                                 imgUrl : this.props.image ,
                                 color : this.props.color ,
                                 price : this.props.price ,
                             }]
                     } );
                 }}>
                {/* eslint-disable-next-line jsx-a11y/alt-text */}
                <img className={"article-image"} src={`/assets/${this.props.image}`} alt={"shoe"}/>
                <div className={"flex space-between"}>
                    <div>
                        {this.props.name}
                    </div>
                    <div>
                        {this.props.price}
                    </div>
                </div>
                {this.props.color}
            </div>)
    }
}

const mapStateToProps = (state) => {
    return ({
        men : state ? state.men : '' ,
        women : state ? state.women : '' ,
        active : state ? state.active : '' ,
        activated : state ? state.activated : '' ,
        cart : state ? state.cart : ''
    });
};
const mapDispatchToProps = (dispatch) => {
    return ({
        addToCart : (params) => {
            dispatch ( addToCart () ( params ) );
        }
    })
};
export const Article = connect ( mapStateToProps , mapDispatchToProps ) ( ArticleComponent );
