        color : 'Nero' ,
        price : '550' ,
    } , {
        name : 'Court' ,
        imgUrl : 'Womens-194-RoyaleKnit-Grey.jpg' ,
        color : 'Red' ,
        price : '320' ,
    }] ,
    active : [{
        name : 'Royale' ,
        imgUrl : 'Mens-193-Royale-Chocolate.jpg' ,
        color : 'Navy' ,
        price : '140' ,
    } , {
        name : 'Royale' ,
        imgUrl : 'Mens-193-Royale-Blanco.jpg' ,
        color : 'Bianco' ,
        price : '120' ,
    } , {
        name : 'Royale' ,
        imgUrl : 'Mens-193-Royale-Nero.jpg' ,
        color : 'Nero' ,
        price : '110' ,
    } , {
        name : 'Court' ,
        imgUrl : 'Mens-193-Royale-Cuoio.jpg' ,
        color : 'Gray' ,
        price : '220' ,
    } ,] ,
    activated : "men" ,
    cart : {} ,
    showSidebar : false ,
    singleItem : {}
};

export default function (state = INITIAL_STATE , action) {
    switch (action.type) {
        case Types.setActive:
            return {
                ...state ,
                active : action.active ,
                activated : action.activated
            };
        case Types.addToCart:
            return {
                ...state ,
                cart : { ...state.cart , ...action.cart } ,
            };
        case Types.showSidebar:
            return {
                ...state ,
                showSidebar : action.showSidebar ,
            };
        case Types.editItem:
            var itemKey = Object.keys(action.singleItem)[0];
            return {
                ...state ,
                cart : {
                    ...state.cart ,
                    [itemKey] : { ...state.cart[itemKey],
                    quantity : action.singleItem[itemKey].quantity
                    }
                }
            };
        case Types.deleteItem:
            console.log(action.deleteItem)
            return {
                ...state ,
            };
        default: {
            return {
                ...state
            }
        }
    }
}
