import React from "react";
import './App.scss';
import {connect} from "react-redux";
import Article from "./article-component";

class ArticleListComponent extends React.Component {
    constructor(props) {
        super ( props );
    }

    render() {
        console.log(this.props.men);
        return (
            <div className={"article-list"}>
            {
                this.props.men.map ( (item) => {
                    console.log(item.imgUrl);
                    let article;
                        if(item.name!=='Court'){
                            article=<Article
                                key = {item.name+item.color}
                                name={item.name}
                                color = {item.color}
                                price = {item.price}
                                image = {item.imgUrl}
                            />
                        }
                    return (
                        {article}

                    )
                } )
            }
            </div>
            )
    }
}

const mapStateToProps = (state) => {
    return ({
        men : state ? state.men : '' ,
        women : state ? state.women : '' ,
    });
};
export const ArticleList = connect ( mapStateToProps) ( ArticleListComponent );
