import React from 'react'
import './App.scss';
import {connect} from "react-redux";
import ScrollContainer from 'react-indiana-drag-scroll'
import GalleryItem from "./gallery-item";

class GalleryComponent extends React.Component {
    constructor(props) {
        super ( props );
        this.state = {
            switchClick :false
        };
    }


    render() {
        () => {
            console.log("click");
            console.log(this.state.expand);
            !!this.state.expand ? this.setState ( { expand : false } ) : this.setState ( { expand : true } )
            console.log(this.state.expand);
        }
         return (
            <ScrollContainer className="scroll-container flex vertical-center"
            onClick={()=>{
                !!this.state.switchClick? this.setState({switchClick : false}): this.setState({switchClick : true})
            }}>
                    {
                        this.props.gallery.map ( (item) => {
                            return (
                                <GalleryItem
                                    url={item.url}
                                    expanded={false}
                                    />)
                        } )

                    }
            </ScrollContainer>
        )
    }
}

const mapStateToProps = (state) => {
    return ({
        gallery : state ? state.gallery : '[]'
    });
};


export const Gallery = connect ( mapStateToProps ) ( GalleryComponent );
