import React from "react";
import './App.scss';
import {addToCart , showSidebar} from "./services/Redux.actions";
import {connect} from "react-redux";
import {CartItem} from "./cart-item";
import {faTimesCircle} from "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

class SidebarComponent extends React.Component {
    constructor(props) {
        super ( props );
        this.state = {
            show : false
        }
    }

    render() {
        var items = [];
        var cart = this.props.cart;
        console.log ( this.props.showSidebar );

        function renderItems() {
            for (let item in cart) {
                if (cart.hasOwnProperty ( item )) {
                    console.log ( item );
                    items.push (
                        <CartItem
                            key={item.name + "_" + item.color}
                            name={cart[item].name}
                            color={cart[item].color}
                            price={cart[item].price}
                            image={cart[item].image}
                        />
                    )
                }
            }
            return items
        }

        return (
            <div className={"sidebar" + (!!this.props.showSidebar === false ? " hide-sidebar" : " show-sidebar")}>
                <div className={"sidebar-inner"}>
                    <div className={"flex-center space-between margin-20-10prc"}>
                        <h2>Cart</h2>
                        <FontAwesomeIcon
                            onClick={() => {
                                this.props.showSidebar ( {
                                    showSidebar : false ,
                                } );
                            }}
                            style={{ fontSize : "24px" }} icon={faTimesCircle}/>
                    </div>
                    {renderItems ()}
                </div>
            </div>)
    }
}

const mapStateToProps = (state) => {
    return ({
        men : state ? state.men : '' ,
        women : state ? state.women : '' ,
        active : state ? state.active : '' ,
        activated : state ? state.activated : '' ,
        cart : state ? state.cart : '[]' ,
        showSidebar : state ? state.showSidebar : '[]'
    });
};
const mapDispatchToProps = (dispatch) => {
    return ({
        addToCart : (params) => {
            dispatch ( addToCart ( params ) );
        }
    },
        {
            showSidebar : (params) => {
                dispatch ( showSidebar ( params ) );
            }
        })
};
export const Sidebar = connect ( mapStateToProps , mapDispatchToProps ) ( SidebarComponent );
